          </div>
          <div className="flex items-center gap-2 text-sm text-muted-foreground mt-1">
            <Phone className="h-4 w-4 shrink-0" />
            <span>{patient.guardianPhone}</span>
          </div>
        </div>

        {showActions && (
          <div className="flex gap-2 pt-2">
