import { Card, CardContent } from "@/components/ui/card";
import { type LucideIcon } from "lucide-react";
import { cn } from "@/lib/utils";

interface StatsCardProps {
  title: string;
  value: number | string;
  icon: LucideIcon;
  variant?: "default" | "success" | "warning" | "danger";
  subtitle?: string;
  testId?: string;
}

export function StatsCard({
  title,
  value,
  icon: Icon,
  variant = "default",
  subtitle,
  testId,
}: StatsCardProps) {
